refsection.programlisting:
// Display some additional information
atomsSetConfig("Verbose","True");

// Load the test repository
exec("SCI/modules/atoms/tests/unit_tests/atomsTestUtils.sce", -1);
repository = atomsCreateTestRepository("scene10");

// Install a module
atomsInstall("toolbox_1V6");

// Install a specific version
atomsInstall(["toolbox_2V6" "2.0"]);

// Install several modules
atomsInstall(["toolbox_4V6" "1.0" ; "toolbox_2V6" "1.0"]);

// Install a module in the user section
atomsInstall(["toolbox_5V6"], "user");

// Install a module in the allusers section
// (write access on SCI directory is needed):
atomsInstall(["toolbox_6V6" "1.0";"toolbox_3V6" "1.0"], "allusers");

// Get the list of installed modules:
disp( atomsGetInstalled() );

// Cleaning
atomsRemove(["toolbox_1V6"; ..
             "toolbox_2V6"; ..
             "toolbox_3V6"; ..
             "toolbox_4V6"; ..
             "toolbox_5V6"; ..
             "toolbox_6V6"]);
atomsRepositoryDel(repository);
atomsSystemUpdate();

// Display some additional information
atomsSetConfig("Verbose","True");

// Switch to offline mode
atomsSetConfig("offLine","True");

// Install a module
atomsInstall(SCI+"/modules/atoms/tests/unit_tests/toolbox_7V6_1.0-1.bin.zip");

// Get the list of installed modules:
disp( atomsGetInstalled() );

// Cleaning
atomsRemove("toolbox_7V6");

// Back to default mode
atomsSetConfig("offLine","False");
