covStart(isnan) for i=1:1e5; isnan(i); end covWrite("binary", "TMPDIR/isnan1") // write runtime information covStop(); // clear instrumentation
covStart(isnan) for i=1:1e5; isnan(string(i)); end covWrite("binary", "TMPDIR/isnan2") // write other runtime information covStop();
covMerge(["TMPDIR/isnan1" "TMPDIR/isnan2"], "TMPDIR/isnan") // merge them covWrite("html", "TMPDIR/isnan", "TMPDIR/coverage") // render the information as HTML
