refsect3.programlisting:
covStart(isnan) // activate isnan for performance measurement for i = 1:1e5; isnan(i); end // run it covWrite("html", "TMPDIR/coverage") // write runtime performance results to a directory covStop(); // deactivate runtime measurement mode for all functions
f = figure("infobar_visible", "off", "toolbar_visible", "off", ... "menubar", "none", "default_axes", "off", ... "axes_size", [800 600], ... "layout", "border");
fr = uicontrol(f, "style", "frame", "backgroundcolor", [1 1 1], "layout", "border");
browser = uicontrol(fr, ... "style", "browser", ... "string", fullfile(TMPDIR, "coverage/isnan.html"), ... "tag", "browser"); // Displays results

covStart(isnan) for i=1:1e5; isnan(i); end covWrite("binary", "TMPDIR/isnan1") // write the 1st binary covStop(); // stop the measurement mode
covStart(isnan) for i=1:1e5; isnan(string(i)); end covWrite("binary", "TMPDIR/isnan2") // write other runtime results covStop();
covMerge(["TMPDIR/isnan1" "TMPDIR/isnan2"], "TMPDIR/isnan") // merge them covWrite("html", "TMPDIR/isnan", "TMPDIR/coverage") // render the results as HTML
