refsection.programlisting:
plot(-10:10, -10:10);

sz = gcf().axes_size; // Utilisé pour changer la référence des positions
xpos = [-10 -10 0 10 10];
ypos = [-10 10 0 -10 10];

for i = 1:size(xpos, "*")
    [xp, yp] = xchange(xpos(i), ypos(i), "f2i");
    uicontrol("Style", "pushbutton", "Position", [xp, sz(2)-yp, 70, 20], "String", string(xpos(i)) + "/" + string(ypos(i)));
end
